const urlmaunaloaco2 = 'http://localhost:8080/v3'

export default function V3() {

  const [maunaloaco2, setmaunaloaco2] = useState([])

  useEffect(() => {
    axios.get(urlmaunaloaco2)
      .then((response) => {
        console.log(response.data)
        setmaunaloaco2(response.data)
      }).catch(error => {
        alert(error.response.error)
      })
  }, [])
 

  const v3 = {
    labels: maunaloaco2.map(d => d.year),
    labels: maunaloaco2.map(d => d.yearmonth),
    datasets: [
       {
        label: 'yearly',
        backgroundColor: 'rgba(75,192,0,1)',
        borderColor: 'rgba(0,0,0,255)',
        borderWidth: 1,
        data: maunaloaco2,
        spanGaps: false,
        parsing: {
          xAxisKey: 'year',
          yAxisKey: 'average'
        }
      },/*  {
        label: 'monthly',
        backgroundColor: 'rgba(75,192,0,1)',
        borderColor: 'rgba(0,0,0,255)',
        borderWidth: 1,
        data: maunaloaco2,
        spanGaps: false,
        parsing: {
          xAxisKey: 'year',
          yAxisKey: 'average'
        }
      }, */
       {
        label: 'deseasonalized',
        backgroundColor: 'rgba(75,192,255,255)',
        borderColor: 'rgba(0,0,0,255)',
        borderWidth: 1,
        data: maunaloaco2,
        spanGaps: false,
        parsing: {
          xAxisKey: 'year',
          yAxisKey: 'deseasonalized'
        }
      }, 
    ] 
  }
  const options = {
    type:'line',
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Corrected seasonal CO2 levels in mauna loa ",
      },
    },
 scales: {

      x: {type: 'time'}
    } ,
}


return (
    <div>
      <div>
      <Line options={options} data={v3} />
      </div>
      <p> </p>
    </div>

  )
}
